   String h="";
     try
     {
       Class.forName("com.mysql.jdbc.Driver");
Connection conn = null;


conn = DriverManager.getConnection("jdbc:mysql://localhost:3306/javaproject1","root", "12345");

      
     
    PreparedStatement p11=conn.prepareStatement("select number from javalearner");
     ResultSet u1=p11.executeQuery();
     while(u1.next())
     {
          h=h+u1.getString("number");
         
     }

 }
     catch(Exception e)
             {
                 
             }
     if (h==null)
     {
System.out.println("no id");
     }
     else
     {
         System.out.println(h);
     }
